import axios from "axios";

// 1. Criar uma instância do axios
const instance = axios.create({
    // 2. Definir a baseURL diretamente na criação da instância
    baseURL: process.env.VUE_APP_SCE_API, 
    withCredentials: true, // Manter o withCredentials
    headers: {
        "Content-Type": "application/json", // Definir cabeçalho padrão
    },
});

// 3. Opcional: Manter os interceptadores (se ainda precisar deles)
// instance.interceptors.request.use(
//     config => {
//         // Configurações adicionais por requisição podem ir aqui se necessário
//         return config;
//     },
//     error => Promise.reject(error)
// );

// 4. Exportar a instância configurada
export default instance;

// 5. IMPORTANTE: Definir também a baseURL padrão global (usada pelo AccordionItem)
// Garantir que axios.defaults.baseURL tenha o valor correto
if (process.env.VUE_APP_SCE_API) {
    axios.defaults.baseURL = process.env.VUE_APP_SCE_API;
} else {
    console.error("VUE_APP_SCE_API não está definida no .env do frontend!");
    // Definir um fallback ou lançar um erro, se preferir
    axios.defaults.baseURL = 'http://localhost:3000'; // Fallback para desenvolvimento
}
